package com.example.proyectodeclaseimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.renderscript.Samplerimport android.view.Viewimport android.widget.Buttonimport android.widget.EditTextclass MainActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        val TxHEnt = findViewById<EditText>(R.id.TxEntrada)        val TxMent = findViewById<EditText>(R.id.TxMEntrada)        val TxHSal = findViewById<EditText>(R.id.TxHSalida)        val TxMsal = findViewById<EditText>(R.id.TxMSalida)        val btCal = findViewById<Button>(R.id.btCalcular)        btCal.setOnClickListener(View.OnClickListener {                var Hora1 = TxHEnt.text.toString().toDouble()                var Minuto1 =TxMent.text.toString().toDouble()                var Hora2 = TxHSal.text.toString().toDouble()                var Minuto2 = TxMsal.text.toString().toDouble()                var Horafin = 0                var Minutofin = 0                var HoraTotal = 0                var costo = 0            fun Estacionamiento () {                if (Hora1 <= 24 && Hora1 >= 0) {                    Horafin = (Hora2 - Hora1).toInt()                    costo = (Horafin * 15.toFloat()).toInt()                    if (Minuto1 <= 59 && Minuto1 >= 0) {                        Minutofin = (Minuto1 - Minuto2).toInt()                        if (Minutofin < 0) {                            Minutofin = 60 + Minutofin                            Horafin = Horafin - 1                            if  (Minutofin < 0) {                                Minutofin = 60 + Minutofin                                Horafin = Horafin - 1                            }                        }                    }else {                    }                }else {                }                if (Horafin <= 0 && Minutofin <= 59) {                    costo = (1 * 15.toFloat()).toInt()                } else {                    costo = (Horafin * 15 + Minutofin * .25).toFloat().toInt()                }            }                val intent = Intent (this, Resultado::class.java)                intent.putExtra("Hora de Entrada", Hora1)                intent.putExtra("Minuto de Entrada", Minuto1)                intent.putExtra("Hora de Salida", Hora2)                intent.putExtra("Minuto de Salida", Minuto2)                intent.putExtra("Total de Horas", HoraTotal)                intent.putExtra("Costo total", costo)                startActivity(intent)        })    }}